glsl3.frag:
precision highp float;
vec4 CCSampleWithAlphaSeparated(sampler2D tex, vec2 uv) {
#if CC_USE_EMBEDDED_ALPHA
  return vec4(texture(tex, uv).rgb, texture(tex, uv + vec2(0.0, 0.5)).r);
#else
  return texture(tex, uv);
#endif
}
#if USE_ALPHA_TEST
  layout(std140) uniform ALPHA_TEST_DATA {
    float alphaThreshold;
  };
#endif
layout(std140) uniform CCGlobal {
  highp   vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_nativeSize;
  mediump vec4 cc_probeInfo;
  mediump vec4 cc_debug_view_mode;
};
layout(std140) uniform CCCamera {
  highp   mat4 cc_matView;
  highp   mat4 cc_matViewInv;
  highp   mat4 cc_matProj;
  highp   mat4 cc_matProjInv;
  highp   mat4 cc_matViewProj;
  highp   mat4 cc_matViewProjInv;
  highp   vec4 cc_cameraPos;
  mediump vec4 cc_surfaceTransform;
  mediump vec4 cc_screenScale;
  mediump vec4 cc_exposure;
  mediump vec4 cc_mainLitDir;
  mediump vec4 cc_mainLitColor;
  mediump vec4 cc_ambientSky;
  mediump vec4 cc_ambientGround;
  mediump vec4 cc_fogColor;
  mediump vec4 cc_fogBase;
  mediump vec4 cc_fogAdd;
  mediump vec4 cc_nearFar;
  mediump vec4 cc_viewPort;
};
in vec4 v_color;
#if USE_TEXTURE
  in vec2 uv0;
  uniform sampler2D cc_spriteTexture;
#endif
const float TAU = 6.28318530718;
const int   MAX_ITER = 8;
float waterHighlight(vec2 p, float time, float foaminess)
{
    vec2 i = p;
    float c = 0.0;
    float foaminess_factor = mix(1.0, 6.0, foaminess);
    float inten = 0.005 * foaminess_factor;
    for (int n = 0; n < MAX_ITER; n++)
    {
        float t = time * (1.0 - (3.5 / float(n + 1)));
        i = p + vec2(
            cos(t - i.x) + sin(t + i.y),
            sin(t - i.y) + cos(t + i.x)
        );
        c += 1.0 / length(vec2(
            p.x / (sin(i.x + t)),
            p.y / (cos(i.y + t))
        ));
    }
    c = 0.2 + c / (inten * float(MAX_ITER));
    c = 1.17 - pow(c, 1.4);
    c = pow(abs(c), 8.0);
    return c / sqrt(foaminess_factor);
}
vec4 frag () {
   vec4 o = vec4(1, 1, 1, 1);
  #if USE_TEXTURE
    o *= CCSampleWithAlphaSeparated(cc_spriteTexture, uv0);
    #if IS_GRAY
      float gray  = 0.2126 * o.r + 0.7152 * o.g + 0.0722 * o.b;
      o.r = o.g = o.b = gray;
    #endif
  #endif
    float time = cc_time.x * 0.1 + 23.0;
    vec2 uv = uv0;
    float aspect = cc_screenSize.x / cc_screenSize.y;
    vec2 uv_square = vec2(uv.x * aspect, uv.y);
    float dist_center = pow(2.0 * length(uv - 0.5), 2.0);
    float foaminess = smoothstep(0.4, 1.8, dist_center);
    float clearness = 0.1 + 0.9 * smoothstep(0.1, 0.5, dist_center);
    vec2 p = mod(uv_square * TAU * 1.0, TAU) - 250.0;
    float c = waterHighlight(p, time, foaminess);
    vec3 water_color = v_color.xyz;
    vec3 color = vec3(c);
    color = clamp(color + water_color, 0.0, 1.0);
    color = mix(water_color, color, clearness);
    return vec4(color,c*v_color.w);
}
layout(location = 0) out vec4 cc_FragColor;
void main() { cc_FragColor = frag(); }

